import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function RegisterCourse() {
  const { state } = useLocation();
  const rollNo = state?.rollNo;
  const [courses, setCourses] = useState([]);
  const [selected, setSelected] = useState([]);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await fetch(`http://localhost:5000/courses/${rollNo}`);
        const data = await response.json();

        // Check if the response is an array before setting state
        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          console.error('Courses data is not an array:', data);
        }
      } catch (error) {
        console.error('Error fetching courses:', error);
      }
    };

    if (rollNo) fetchCourses();
  }, [rollNo]);

  const handleCheckboxChange = (e, teacherCourseCode) => {
    if (e.target.checked) {
      setSelected([...selected, teacherCourseCode]);
    } else {
      setSelected(selected.filter(code => code !== teacherCourseCode));
    }
  };

  const handleEnroll = async () => {
    try {
      for (const courseCode of selected) {
        const response = await fetch('http://localhost:5000/enroll', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            roll_no: rollNo,
            teacher_course_code: courseCode,
          }),
        });

        const data = await response.json();
        console.log('Enrolled:', data);
      }

      setSelected([]);
    } catch (error) {
      console.error('Error enrolling:', error);
    }
  };

  // Display a message if no courses are found
  if (courses.length === 0) return <p>No courses found for your semester.</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Courses for Your Semester</h2>
      <table>
        <thead>
          <tr>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Type</th>
            <th>Credit Hours</th>
            <th>Teacher</th>
            <th>Available Seats</th>
            <th>Select</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.teacher_course_code}>
              <td>{course.course_code}</td>
              <td>{course.course_name}</td>
              <td>{course.course_type}</td>
              <td>{course.credit_hours}</td>
              <td>{course.teacher_first_name} {course.teacher_last_name}</td>
              <td>{course.available_seats}</td>
              <td>
                <input
                  type="checkbox"
                  checked={selected.includes(course.teacher_course_code)}
                  onChange={(e) => handleCheckboxChange(e, course.teacher_course_code)}
                  disabled={course.available_seats <= 0}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleEnroll} disabled={selected.length === 0}>
        Enroll
      </button>
    </div>
  );
}

export default RegisterCourse;
