import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SeeEnrolled = () => {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('');
  const [students, setStudents] = useState([]);

  useEffect(() => {
    // Fetch teacher_course_codes from the Enrolled table
    axios.get('http://localhost:5000/api/fetchteachercoursecode')
      .then(response => {
        setCourses(response.data);
      })
      .catch(error => {
        console.error('Error fetching teacher course codes:', error);
      });
  }, []);

  useEffect(() => {
    if (selectedCourse) {
      axios.get(`http://localhost:5000/api/enrolledstudents/${selectedCourse}`)
        .then(response => {
          setStudents(response.data);
        })
        .catch(error => {
          console.error('Error fetching enrolled students:', error);
        });
    } else {
      setStudents([]); // Clear student list if no course selected
    }
  }, [selectedCourse]);

  const handleSelectChange = (e) => {
    setSelectedCourse(e.target.value);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Enrolled Students</h1>
      <label htmlFor="courseSelect">Select Course:</label>
      <select id="courseSelect" onChange={handleSelectChange} value={selectedCourse}>
        <option value="">-- Select a course --</option>
        {courses.map((course, index) => (
          <option key={index} value={course.teacher_course_code}>
            {course.teacher_course_code}
          </option>
        ))}
      </select>

      {students.length > 0 && (
        <table style={{ marginTop: '20px', borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              <th>Roll No</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Batch</th>
              <th>Semester</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student.roll_no}>
                <td>{student.roll_no}</td>
                <td>{student.first_name}</td>
                <td>{student.last_name}</td>
                <td>{student.batch}</td>
                <td>{student.semester}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default SeeEnrolled;
