import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function RegisterCourse() {
  const { state } = useLocation();
  const rollNo = state?.rollNo;
  const [courses, setCourses] = useState([]);
  const [selected, setSelected] = useState([]);

  useEffect(() => {
    const fetchCourses = async () => {
      const response = await fetch(`http://localhost:5000/courses/${rollNo}`);
      const data = await response.json();
      setCourses(data);
    };

    if (rollNo) fetchCourses();
  }, [rollNo]);

  const handleCheckboxChange = (code) => {
    setSelected((prev) =>
      prev.includes(code) ? prev.filter((c) => c !== code) : [...prev, code]
    );
  };

  const handleEnroll = async () => {
    if (selected.length === 0) {
      alert("Please select at least one course to enroll.");
      return;
    }

    const response = await fetch('http://localhost:5000/enroll', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ roll_no: rollNo, selectedCourses: selected })
    });

    const result = await response.json();
    if (response.ok) {
      alert("✅ Enrollment successful");
      setSelected([]); // clear selection
    } else {
      alert("❌ Enrollment failed: " + result.error);
    }
  };

  if (courses.length === 0) return <p>No courses found for your semester.</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Courses for Your Semester</h2>
      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Type</th>
            <th>Credit Hours</th>
            <th>Teacher</th>
            <th>Available Seats</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.teacher_course_code}>
              <td>
                <input
                  type="checkbox"
                  checked={selected.includes(course.teacher_course_code)}
                  onChange={() => handleCheckboxChange(course.teacher_course_code)}
                  disabled={course.available_seats <= 0}
                />
              </td>
              <td>{course.course_code}</td>
              <td>{course.course_name}</td>
              <td>{course.course_type}</td>
              <td>{course.credit_hours}</td>
              <td>{course.teacher_name}</td>
              <td>{course.available_seats}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleEnroll} style={{ marginTop: '20px' }}>
        Enroll
      </button>
    </div>
  );
}

export default RegisterCourse;
