import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './ChangeEnrolled.css'; // Reuse ChangeCourse.css or create identical styles

const ChangeEnrolled = () => {
  const [operation, setOperation] = useState(""); // "add", "delete"
  const [message, setMessage] = useState('');
  const [formData, setFormData] = useState({
    teacherCourseCode: "",
    rollNumber: ""
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let endpoint = '';
      let successMessage = '';

      if (operation === "add") {
        endpoint = 'add';
        successMessage = 'Enrollment added successfully';
      } else if (operation === "delete") {
        endpoint = 'delete';
        successMessage = 'Enrollment deleted successfully';
      }

      const response = await fetch(`http://localhost:5000/changeenrolled/${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setMessage(successMessage);
        setTimeout(() => {
          setMessage('');
          setOperation('');
          setFormData({
            teacherCourseCode: "",
            rollNumber: ""
          });
        }, 2000);
      } else {
        throw new Error('Operation failed');
      }
    } catch (err) {
      console.error("❌ Fetch failed:", err);
      setMessage('Operation failed. Please try again.');
    }
  };

  return (
    <div className="change-course-container"> {/* Same class name as ChangeCourse */}
      <div className="change-course-card">
        <h1 className="change-course-header">Enrollment Management</h1>

        <div className="operation-buttons">
          <button 
            className="operation-selector"
            onClick={() => setOperation("add")}
          >
            Add Enrollment
          </button>
          <button 
            className="operation-selector"
            onClick={() => setOperation("delete")}
          >
            Delete Enrollment
          </button>
        </div>

        {operation && (
          <form onSubmit={handleSubmit} className="course-form"> {/* Same class name */}
            <div className="form-group">
              <label htmlFor="teacherCourseCode">Teacher Course Code</label>
              <input
                type="text"
                id="teacherCourseCode"
                name="teacherCourseCode"
                value={formData.teacherCourseCode}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Enter teacher course code"
              />
            </div>

            <div className="form-group">
              <label htmlFor="rollNumber">Roll Number</label>
              <input
                type="text"
                id="rollNumber"
                name="rollNumber"
                value={formData.rollNumber}
                onChange={handleChange}
                required
                className="form-input"
                placeholder="Enter student roll number"
              />
            </div>

            <button 
              type="submit" 
              className={`submit-button ${operation}`}
            >
              {operation === "add" && "Add Enrollment"}
              {operation === "delete" && "Delete Enrollment"}
            </button>
          </form>
        )}

        {message && (
          <p className={`message ${message.includes('failed') ? 'error-message' : 'success-message'}`}>
            {message}
          </p>
        )}
      </div>
    </div>
  );
};

export default ChangeEnrolled;
