import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const ChangeCourse = () => {
  const [operation, setOperation] = useState(""); // "add", "delete", "update"
  const [formData, setFormData] = useState({
    courseCode: "",
    courseName:"",
    courseType: "",
    creditHours: "",
    department: "",
    preReq:""
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (operation === "add") {
              try {
                const response = await fetch('http://localhost:5000/changecourse/add', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify({   courseCode: formData.courseCode,
                    courseName:formData.courseName,
                    courseType: formData.courseType,
                    creditHours: formData.creditHours,
                    department: formData.department,
                    preReq:formData.preReq }),
                });

                if (response.ok) {
                  alert("Course added successfully.");
                  //setSelectedCourses([]);
                  setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
                }
              } catch (err) {
                console.error("❌ Fetch failed:", err);
              }
      // call add API
      console.log("Add course:", formData);
    } else if (operation === "delete") {
      // call delete API
      try {
        const response = await fetch('http://localhost:5000/changecourse/delete', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({   courseCode: formData.courseCode,
            courseName:formData.courseName,
            courseType: formData.courseType,
            creditHours: formData.creditHours,
            department: formData.department,
            preReq:formData.preReq }),
        });

        if (response.ok) {
          alert("Course deleted successfully.");
          //setSelectedCourses([]);
          setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
        }
      } catch (err) {
        console.error("❌ Fetch failed:", err);
      }
      console.log("Delete course:", formData.courseCode);
    } else if (operation === "update") {
      try {
        const response = await fetch('http://localhost:5000/changecourse/update', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({   courseCode: formData.courseCode,
            courseName:formData.courseName,
            courseType: formData.courseType,
            creditHours: formData.creditHours,
            department: formData.department,
            preReq:formData.preReq }),
        });

        if (response.ok) {
          alert("Course updated successfully.");
          //setSelectedCourses([]);
          setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
        }
      } catch (err) {
        console.error("❌ Fetch failed:", err);
      }
      // call update API
      console.log("Update course:", formData);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Course Management</h2>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <button onClick={() => setOperation("add")}>Add Course</button>
        <button onClick={() => setOperation("delete")}>Delete Course</button>
        <button onClick={() => setOperation("update")}>Update Course</button>
      </div>

      {operation && (
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '400px' }}>
          <label>
            Course Code:
            <input
              type="text"
              name="courseCode"
              value={formData.courseCode}
              onChange={handleChange}
              required
            />
          </label>

          {(operation === "add" || operation === "update") && (
            <>
                  <label>
                Course Name:
                <input
                  type="text"
                  name="courseName"
                  value={formData.courseName}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                Course Type:
                <input
                  type="text"
                  name="courseType"
                  value={formData.courseType}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                Credit Hours:
                <input
                  type="number"
                  name="creditHours"
                  value={formData.creditHours}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                Pre-req:
                <input
                  type="text"
                  name="preReq"
                  value={formData.preReq}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                Department:
                <input
                  type="text"
                  name="department"
                  value={formData.department}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
            </>
          )}

          <button type="submit">
            {operation === "add" && "Add Course"}
            {operation === "delete" && "Delete Course"}
            {operation === "update" && "Update Course"}
          </button>
        </form>
      )}
    </div>
  );


}

export default ChangeCourse;
