import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const ChangeTeacher = () => {
  const [operation, setOperation] = useState(""); // "add", "delete", "update"
  const [formData, setFormData] = useState({
    firstName: "",
    lastName:"",
    teacherId: "",
    email: ""
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (operation === "add") {
              try {
                const response = await fetch('http://localhost:5000/changeteacher/add', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify({ firstName: formData.firstName,
                    lastName:formData.lastName,
                    teacherId: formData.teacherId,
                    email:formData.email  }),
                });

                if (response.ok) {
                  alert("teacher added successfully.");
                  //setSelectedCourses([]);
                  setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
                }
              } catch (err) {
                console.error("❌ Fetch failed:", err);
              }
      // call add API
      console.log("Add teacher:", formData);
    } else if (operation === "delete") {
      // call delete API
      try {
        const response = await fetch('http://localhost:5000/changeteacher/delete', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({  firstName: formData.firstName,
            lastName:formData.lastName,
            teacherId: formData.teacherId,
            email:formData.email }),
        });

        if (response.ok) {
          alert("teacher deleted successfully.");
          //setSelectedCourses([]);
          setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
        }
      } catch (err) {
        console.error("❌ Fetch failed:", err);
      }
      console.log("Delete teacher:", formData.courseCode);
    } else if (operation === "update") {
      try {
        const response = await fetch('http://localhost:5000/changeteacher/update', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({  firstName: formData.firstName,
            lastName:formData.lastName,
            teacherId: formData.teacherId,
            email:formData.email }),
        });

        if (response.ok) {
          alert("teacher updated successfully.");
          //setSelectedCourses([]);
          setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
        }
      } catch (err) {
        console.error("❌ Fetch failed:", err);
      }
      // call update API
      console.log("Update teacher:", formData);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>teacher Management</h2>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <button onClick={() => setOperation("add")}>Add Teacher</button>
        <button onClick={() => setOperation("delete")}>Delete Teacher</button>
        <button onClick={() => setOperation("update")}>Update Teacher</button>
      </div>

      {operation && (
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '400px' }}>
          <label>
            Teacher Code:
            <input
              type="text"
              name="teacherId"
              value={formData.teacherId}
              onChange={handleChange}
              required
            />
          </label>

          {(operation === "add" || operation === "update") && (
            <>
                  <label>
                first Name:
                <input
                  type="text"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                Last Name:
                <input
                  type="text"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                email:
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
            </>
          )}

          <button type="submit">
            {operation === "add" && "Add Teacher"}
            {operation === "delete" && "Delete Teacher"}
            {operation === "update" && "Update Teacher"}
          </button>
        </form>
      )}
    </div>
  );


}



export default ChangeTeacher;
