import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function AdminDashboard() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const rollNo = state?.rollNo;

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('token');  // Clear JWT
    navigate('/admin');  // Redirect to login page
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      {/* <h2>Welcome, {student.first_name} {student.last_name}</h2>
      <p><strong>Roll No:</strong> {student.roll_no}</p>
      <p><strong>Department ID:</strong> {student.department_id}</p>
      <p><strong>Batch:</strong> {student.batch}</p>
      <p><strong>Semester:</strong> {student.semester}</p>
      <p><strong>Email:</strong> {student.email}</p> */}

      <div style={{ marginTop: '30px' }}>
        <button onClick={() => navigate('/changecourse', { state: { rollNo } })}>Change Course info</button>
        <button onClick={() => navigate('/changesection', { state: { rollNo } })}>Change Section info</button>
        <button onClick={() => navigate('/changeteacher', { state: { rollNo } })}>change Teacher Info</button>
        <button onClick={() => navigate('/changestudent', { state: { rollNo } })}>change Student Info</button>
        <button onClick={() => navigate('/changelogin', { state: { rollNo } })}>change Student Log in Info</button>
        <button onClick={() => navigate('/changeadmin', { state: { rollNo } })}>change Admin Info</button>
        <button onClick={() => navigate('/changegrade', { state: { rollNo } })}>change Grade/Transcript Info</button>
        <button onClick={() => navigate('/changeenrolled', { state: { rollNo } })}>change Enrollment Info</button>
        <br /><br />
        <button onClick={handleLogout} style={{ backgroundColor: 'red', color: 'white' }}>
          Logout
        </button>
      </div>
    </div>
  );
}

export default AdminDashboard;
