import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const ChangeStudent = () => { 
  const [operation, setOperation] = useState(""); // "add", "delete", "update"
  const [formData, setFormData] = useState({
    firstName: "",
    lastName:"",
    rollNumber:"",
    departmentId: "",
    batch:"",
    semester:"",
    email: ""
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (operation === "add") {
              try {
                const response = await fetch('http://localhost:5000/changestudent/add', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify({ firstName: formData.firstName,
                    lastName:formData.lastName,
                      rollNumber:formData.rollNumber,
                    departmentId: formData.departmentId,
                    batch:formData.batch,
                    semester:formData.semester,
                    email: formData.email  }),
                });

                if (response.ok) {
                  alert("student added successfully.");
                  //setSelectedCourses([]);
                  setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
                }
              } catch (err) {
                console.error("❌ Fetch failed:", err);
              }
      // call add API
      console.log("Add student:", formData);
    } else if (operation === "delete") {
      // call delete API
      try {
        const response = await fetch('http://localhost:5000/changestudent/delete', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({  firstName: formData.firstName,
            lastName:formData.lastName,
            rollNumber:formData.rollNumber,
            departmentId: formData.departmentId,
            batch:formData.batch,
            semester:formData.semester,
            email: formData.email}),
        });

        if (response.ok) {
          alert("student deleted successfully.");
          //setSelectedCourses([]);
          setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
        }
      } catch (err) {
        console.error("❌ Fetch failed:", err);
      }
      console.log("Delete student:", formData.courseCode);
    } else if (operation === "update") {
      try {
        const response = await fetch('http://localhost:5000/changestudent/update', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({  firstName: formData.firstName,
            lastName:formData.lastName,
            rollNumber:formData.rollNumber,
            departmentId: formData.departmentId,
            batch:formData.batch,
            semester:formData.semester,
            email: formData.email }),
        });

        if (response.ok) {
          alert("student updated successfully.");
          //setSelectedCourses([]);
          setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
        }
      } catch (err) {
        console.error("❌ Fetch failed:", err);
      }
      // call update API
      console.log("Update student:", formData);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Student Management</h2>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <button onClick={() => setOperation("add")}>Add Student</button>
        <button onClick={() => setOperation("delete")}>Delete Student</button>
        <button onClick={() => setOperation("update")}>Update Student</button>
      </div>

      {operation && (
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '400px' }}>
          <label>
            Roll number:
            <input
              type="text"
              name="rollNumber"
              value={formData.rollNumber}
              onChange={handleChange}
              required
            />
          </label>

          {(operation === "add" || operation === "update") && (
            <>
                  <label>
                first Name:
                <input
                  type="text"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                Last Name:
                <input
                  type="text"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                department id:
                <input
                  type="text"
                  name="departmentId"
                  value={formData.departmentId}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                batch
                <input
                  type="number"
                  name="batch"
                  value={formData.batch}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                semester:
                <input
                  type="number"
                  name="semester"
                  value={formData.semester}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
              <label>
                email:
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required={operation === "add"}
                />
              </label>
            </>
          )}

          <button type="submit">
            {operation === "add" && "Add Student"}
            {operation === "delete" && "Delete Student"}
            {operation === "update" && "Update Student"}
          </button>
        </form>
      )}
    </div>
  );


}

export default ChangeStudent;
