import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function RegisterCourse() {
  const { state } = useLocation();
  const rollNo = state?.rollNo;

  const [courses, setCourses] = useState([]);
  const [selected, setSelected] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await fetch(`http://localhost:5000/courses/${rollNo}`);
        const data = await response.json();

        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          console.error("Unexpected response format:", data);
          setCourses([]);
        }
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };

    if (rollNo) fetchCourses();
  }, [rollNo]);

  const handleCheckboxChange = (e, code) => {
    if (e.target.checked) {
      setSelected((prev) => [...prev, code]);
    } else {
      setSelected((prev) => prev.filter((c) => c !== code));
    }
  };

  const getRelatedPairCode = (code) => {
    if (code.endsWith("L")) return code.slice(0, -1); // lab to course
    else return code + "L"; // course to lab
  };

  const handleEnroll = async () => {
    // Validation for course + lab pairing
    for (const code of selected) {
      const pairCode = getRelatedPairCode(code);
      const isPairInSemester = courses.some(
        (course) => course.teacher_course_code === pairCode
      );
      const isPairSelected = selected.includes(pairCode);

      if (isPairInSemester && !isPairSelected) {
        setMessage(`You must enroll in both the course and its lab for ${code}.`);
        return;
      }
    }

    try {
      const response = await fetch("http://localhost:5000/enroll", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ roll_no: rollNo, selectedCourses: selected }),
      });

      const data = await response.json();
      if (response.ok) {
        setMessage("Enrollment successful!");
        setSelected([]);
        setTimeout(() => window.location.reload(), 1500);
      } else {
        setMessage(data.error || "Enrollment failed.");
      }
    } catch (error) {
      console.error("Error during enrollment:", error);
      setMessage("Server error during enrollment.");
    }
  };

  if (!Array.isArray(courses) || courses.length === 0) {
    return <p>No courses found for your semester.</p>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Courses for Your Semester</h2>

      {message && <p><strong>{message}</strong></p>}

      <table border="1" cellPadding="8" cellSpacing="0">
        <thead>
          <tr>
            <th>Select</th>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Type</th>
            <th>Credit Hours</th>
            <th>Teacher</th>
            <th>Available Seats</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {courses.map((course) => (
            <tr key={course.teacher_course_code}>
              <td>
                <input
                  type="checkbox"
                  checked={
                    course.already_enrolled === 1 ||
                    selected.includes(course.teacher_course_code)
                  }
                  onChange={(e) => handleCheckboxChange(e, course.teacher_course_code)}
                  disabled={course.available_seats <= 0 || course.already_enrolled === 1}
                />
              </td>
              <td>{course.course_code}</td>
              <td>{course.course_name}</td>
              <td>{course.course_type}</td>
              <td>{course.credit_hours}</td>
              <td>{course.teacher_name}</td>
              <td>{course.available_seats}</td>
              <td>
                {course.already_enrolled === 1
                  ? "Already Enrolled"
                  : course.available_seats <= 0
                  ? "Full"
                  : "Available"}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button
        onClick={handleEnroll}
        disabled={selected.length === 0}
        style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}
      >
        Enroll
      </button>
    </div>
  );
}

export default RegisterCourse;
