import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function Dashboard() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const rollNo = state?.rollNo;
  const [student, setStudent] = useState(null);

  useEffect(() => {
    const fetchStudent = async () => {
      const response = await fetch(`http://localhost:5000/student/${rollNo}`);
      const data = await response.json();
      setStudent(data);
    };

    if (rollNo) fetchStudent();
  }, [rollNo]);

  if (!student) return <p>Loading...</p>;

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h2>Welcome, {student.first_name} {student.last_name}</h2>
      <p><strong>Roll No:</strong> {student.roll_no}</p>
      <p><strong>Department ID:</strong> {student.department_id}</p>
      <p><strong>Batch:</strong> {student.batch}</p>
      <p><strong>Semester:</strong> {student.semester}</p>
      <p><strong>Email:</strong> {student.email}</p>

      <div style={{ marginTop: '30px' }}>
        <button onClick={() => navigate('/transcript', { state: { rollNo } })}>Transcript</button>
        <button onClick={() => navigate('/register', { state: { rollNo } })}>Course Registration</button>
        <button onClick={() => navigate('/swap', { state: { rollNo } })}>Swap Section</button>
      </div>
    </div>
  );
}

export default Dashboard;
