import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function RegisterCourse() {
  const { state } = useLocation();
  const rollNo = state?.rollNo;

  const [courses, setCourses] = useState([]);
  const [selected, setSelected] = useState([]);
  const [message, setMessage] = useState([]);
  const [enrolledCourses, setEnrolledCourses] = useState([]);

  // Fetch courses
  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await fetch(`http://localhost:5000/courses/${rollNo}`);
        const data = await response.json();

        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          console.error("Unexpected response format:", data);
          setCourses([]);
        }
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };

    if (rollNo) fetchCourses();
  }, [rollNo]);

  // Fetch enrolled courses to disable already enrolled options
  useEffect(() => {
    const fetchEnrolledCourses = async () => {
      try {
        const response = await fetch(`http://localhost:5000/enrolled/${rollNo}`);
        const data = await response.json();

        if (Array.isArray(data)) {
          setEnrolledCourses(data);
        } else {
          console.error("Error fetching enrolled courses:", data);
          setEnrolledCourses([]);
        }
      } catch (error) {
        console.error("Error fetching enrolled courses:", error);
      }
    };

    if (rollNo) fetchEnrolledCourses();
  }, [rollNo]);

  // Handle checkbox toggle
  const handleCheckboxChange = (e, code) => {
    if (e.target.checked) {
      setSelected((prev) => [...prev, code]);
    } else {
      setSelected((prev) => prev.filter((c) => c !== code));
    }
  };

  // Handle enrollment
  const handleEnroll = async () => {
    const selectedCourseDetails = courses.filter((course) =>
      selected.includes(course.teacher_course_code)
    );

    const selectedCourseCodes = selectedCourseDetails.map((c) => c.course_code);

    // Step 1: Check for multiple instances of the same course (if same course selected with multiple teachers)
    const uniqueCourses = new Set(selectedCourseCodes);
    if (uniqueCourses.size !== selectedCourseCodes.length) {
      const msg = "You cannot enroll in multiple sections of the same course.";
      alert(msg); // Show alert popup
      setMessage(msg); // Show message on screen
      return;
    }

    // Step 2: Ensure student enrolls in both the course and its lab if they select either
    for (const code of selectedCourseCodes) {
      const isLab = code.endsWith("L");
      const baseCode = isLab ? code.slice(0, -1) : code + "L";

      const pairExists = courses.some((c) => c.course_code === baseCode);
      const pairSelected = selectedCourseCodes.includes(baseCode);

      if (pairExists && !pairSelected) {
        const msg = `You must enroll in both the course and its lab for ${code}.`;
        alert(msg); // Show popup
        setMessage(msg); // Optionally show on screen
        return;
      }
    }

    // Step 3: Submit the enrollment request
    try {
      const response = await fetch("http://localhost:5000/enroll", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ roll_no: rollNo, selectedCourses: selected }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("Enrollment successful!"); // Success popup
        setMessage("Enrollment successful!");
        setSelected([]);
        setTimeout(() => window.location.reload(), 1500);
      } else {
        alert(data.error || "Enrollment failed."); // Error popup
        setMessage(data.error || "Enrollment failed.");
      }
    } catch (error) {
      console.error("Error during enrollment:", error);
      alert("Server error during enrollment.");
      setMessage("Server error during enrollment.");
    }
  };

  // Check if a teacher-course combination is already enrolled
  const isAlreadyEnrolled = (teacherCourseCode) => {
    return enrolledCourses.some(
      (enrolled) => enrolled.teacher_course_code === teacherCourseCode
    );
  };

  if (!Array.isArray(courses) || courses.length === 0) {
    return <p>No courses found for your semester.</p>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Courses for Your Semester</h2>

      {message && <p><strong>{message}</strong></p>}

      <table border="1" cellPadding="8" cellSpacing="0">
        <thead>
          <tr>
            <th>Select</th>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Type</th>
            <th>Credit Hours</th>
            <th>Teacher</th>
            <th>Available Seats</th>
          </tr>
        </thead>
        <tbody>
          {courses.map((course) => (
            <tr key={course.teacher_course_code}>
              <td>
                <input
                  type="checkbox"
                  checked={selected.includes(course.teacher_course_code)}
                  onChange={(e) => handleCheckboxChange(e, course.teacher_course_code)}
                  disabled={isAlreadyEnrolled(course.teacher_course_code) || course.available_seats <= 0}
                />
              </td>
              <td>{course.course_code}</td>
              <td>{course.course_name}</td>
              <td>{course.course_type}</td>
              <td>{course.credit_hours}</td>
              <td>{course.teacher_name}</td>
              <td>{course.available_seats}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <button
        onClick={handleEnroll}
        disabled={selected.length === 0}
        style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}
      >
        Enroll
      </button>
    </div>
  );
}

export default RegisterCourse;
