import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SeeEnrolled = () => {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('');

  useEffect(() => {
    // Fetch teacher_course_codes from the Enrolled table using the updated API
    axios.get('http://localhost:5000/api/fetchteachercoursecode')
      .then(response => {
        setCourses(response.data);
      })
      .catch(error => {
        console.error('Error fetching teacher course codes:', error);
      });
  }, []);

  const handleSelectChange = (e) => {
    setSelectedCourse(e.target.value);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Enrolled Students</h1>
      <label htmlFor="courseSelect">Select Course:</label>
      <select id="courseSelect" onChange={handleSelectChange} value={selectedCourse}>
        <option value="">-- Select a course --</option>
        {courses.map((course, index) => (
          <option key={index} value={course.teacher_course_code}>
            {course.teacher_course_code}
          </option>
        ))}
      </select>
    </div>
  );
};

export default SeeEnrolled;
