import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseStudentList = () => {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('');
  const [students, setStudents] = useState([]);

  useEffect(() => {
    // Fetch all courses to populate the dropdown
    axios.get('http://localhost:5000/views')  // Modify this endpoint to get course list
      .then(response => {
        // Assuming 'Courses' is part of the response data structure
        setCourses(response.data.Courses);
      })
      .catch(error => {
        console.error('Error fetching courses:', error);
      });
  }, []);

  const handleCourseSelect = (courseCode) => {
    setSelectedCourse(courseCode);

    // Fetch students enrolled in the selected course
    axios.get(`http://localhost:5000/course/${courseCode}/students`)
      .then(response => {
        setStudents(response.data);
      })
      .catch(error => {
        console.error('Error fetching students:', error);
      });
  };

  return (
    <div>
      <h1>Course Students</h1>

      <label htmlFor="courseSelect">Select Course: </label>
      <select
        id="courseSelect"
        value={selectedCourse}
        onChange={(e) => handleCourseSelect(e.target.value)}
      >
        <option value="">--Select a course--</option>
        {courses.map((course) => (
          <option key={course.course_code} value={course.course_code}>
            {course.course_name}
          </option>
        ))}
      </select>

      {students.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Roll No</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Batch</th>
              <th>Semester</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student.roll_no}>
                <td>{student.roll_no}</td>
                <td>{student.first_name}</td>
                <td>{student.last_name}</td>
                <td>{student.batch}</td>
                <td>{student.semester}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default CourseStudentList;
