import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom'; // Use useNavigate instead of useHistory

function EnrolledCourses () {
  const { state } = useLocation();
  const rollNumber = state?.rollNo;

  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState([]);

  // Fetch enrolled courses on mount or when rollNumber changes
  useEffect(() => {
    const fetchEnrolledCourses = async () => {
      try {
        const response = await fetch(`http://localhost:5000/enrolled-courses/${rollNumber}`);
        const data = await response.json();

        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          console.error("Unexpected response format:", data);
          setCourses([]);
        }
      } catch (error) {
        console.error('Error fetching enrolled courses:', error);
      }
    };

    if (rollNumber) {
      fetchEnrolledCourses();
    }
  }, [rollNumber]);

  // Handle checkbox selection
  const handleCheckboxChange = (courseCode) => {
    setSelectedCourses((prevSelected) =>
      prevSelected.includes(courseCode)
        ? prevSelected.filter((code) => code !== courseCode)
        : [...prevSelected, courseCode]
    );
  };

  // Handle course drop
  const handleDrop = async () => {
    try {
      const response = await fetch('http://localhost:5000/drop-courses', {
        method: 'POST',
        headers: {'Content-Type': 'application/json' },
        body: JSON.stringify({rollNumber, courses: selectedCourses}),
      });

      if (response.ok) {
        // Filter out dropped courses from the list
        setCourses((prevCourses) =>
          prevCourses.filter((course) => !selectedCourses.includes(course.Course_code))
        );
        setSelectedCourses([]); // Reset selected courses
      } else {
        console.error('Failed to drop courses');
      }
    } catch (error) {
      console.error('Error dropping courses:', error);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Your Enrolled Courses</h2>
      <table className="w-full border">
        <thead>
          <tr>
            <th>Select</th>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Elective</th>
          </tr>
        </thead>
        <tbody>
          {courses.length > 0 ? (
            courses.map((course) => (
              <tr key={course.teacher_course_code}>
                <td>
                  <input
                    type="checkbox"
                    disabled={course.Type !== 'Elective'} // Disable checkbox for non-elective courses
                    checked={selectedCourses.includes(course.Course_code)}
                    onChange={() => handleCheckboxChange(course.Course_code)}
                  />
                </td>
                <td>{course.Course_code}</td>
                <td>{course.Course_name}</td>
                <td>{course.Type}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" className="text-center">No enrolled courses available.</td>
            </tr>
          )}
        </tbody>
      </table>
      <button
        onClick={handleDrop}
        className="mt-4 bg-red-500 text-white px-4 py-2 rounded"
        disabled={selectedCourses.length === 0}
      >
        Drop Selected
      </button>
    </div>
  );
};

export default EnrolledCourses;
