import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom'

const ChangeAdmin = () => {

 const { state } = useLocation();
 const navigate = useNavigate();
 const rollNo = state?.rollNo;
//  const [newadminId,setNewadminId]=useState('');
//  const [oldPassword, setOldPassword] = useState('');
//  const [newPassword, setNewPassword] = useState('');
//  const [confirmNewPassword, setConfirmNewPassword] = useState('');
//  const [error, setError] = useState('');
 const [message, setMessage] = useState('');
    const [operation, setOperation] = useState(""); // "add", "delete", "update"
    const [formData, setFormData] = useState({
      adminId: "",
      newPassword: "",
    });

    const handleChange = (e) => {
      setFormData(prev => ({
        ...prev,
        [e.target.name]: e.target.value
      }));
    };

    const handleSubmit = async (e) => {
      e.preventDefault();
      if (operation === "add") {
                try {
                  const response = await fetch('http://localhost:5000/changeadmin/add', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({  adminId:formData.adminId,
                        newPassword:formData.newPassword
                     }),
                  });

                  if (response.ok) {
                    alert("Admin added successfully.");
                    //setSelectedCourses([]);
                    setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
                  }
                } catch (err) {
                  console.error("❌ Fetch failed:", err);
                }
        // call add API
        console.log("Add Admin:", formData);
      } else if (operation === "delete") {
        // call delete API
        try {
          const response = await fetch('http://localhost:5000/changeadmin/delete', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({   adminId:formData.adminId,
                newPassword:formData.newPassword}),
          });

          if (response.ok) {
            alert("Admin deleted successfully.");
            //setSelectedCourses([]);
            setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
          }
        } catch (err) {
          console.error("❌ Fetch failed:", err);
        }
        console.log("Delete admin:", formData);
      } else if (operation === "update") {
        try {
          const response = await fetch('http://localhost:5000/changeadmin/update', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({   adminId:formData.adminId,
                newPassword:formData.newPassword }),
          });

          if (response.ok) {
            alert("Admin updated successfully.");
            //setSelectedCourses([]);
            setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
          }
        } catch (err) {
          console.error("❌ Fetch failed:", err);
        }
        // call update API
        console.log("Update Admin:", formData);
      }
    };

    return (
      <div style={{ padding: '20px' }}>
        <h2>Admin Management</h2>
        <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
          <button onClick={() => setOperation("add")}>Add Admin</button>
          <button onClick={() => setOperation("delete")}>Delete Admin</button>
          <button onClick={() => setOperation("update")}>Update Admin</button>
        </div>

        {operation && (
          <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '400px' }}>
            <label>
              Admin ID:
              <input
                type="text"
                name="adminId"
                value={formData.adminId}
                onChange={handleChange}
                required
              />
            </label>

            {(operation === "add" || operation === "update") && (
              <>
                <label>
                  Password:
                  <input
                    type="text"
                    name="newPassword"
                    value={formData.newPassword}
                    onChange={handleChange}
                    required
                  />
                </label>
              </>
            )}

            <button type="submit">
              {operation === "add" && "Add Admin"}
              {operation === "delete" && "Delete Admin"}
              {operation === "update" && "Update Admin"}
            </button>
          </form>
        )}

      </div>
    );


  }



export default ChangeAdmin
