import React, { useState } from 'react';
import axios from 'axios';
import './ChangeGrade.css';

const ChangeGrade = () => {
  const [operation, setOperation] = useState('add');
  const [formData, setFormData] = useState({
    studentId: '',
    courseId: '',
    sectionId: '',
    semester: '',
    year: '',
    grade: ''
  });
  const [message, setMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let response;
      switch (operation) {
        case 'add':
          response = await axios.post('http://localhost:3001/api/grades', formData);
          setMessage('Grade added successfully!');
          break;
        case 'delete':
          response = await axios.delete(`http://localhost:3001/api/grades/${formData.studentId}/${formData.courseId}/${formData.sectionId}/${formData.semester}/${formData.year}`);
          setMessage('Grade deleted successfully!');
          break;
        case 'update':
          response = await axios.put(`http://localhost:3001/api/grades/${formData.studentId}/${formData.courseId}/${formData.sectionId}/${formData.semester}/${formData.year}`, { grade: formData.grade });
          setMessage('Grade updated successfully!');
          break;
        default:
          break;
      }
      setFormData({
        studentId: '',
        courseId: '',
        sectionId: '',
        semester: '',
        year: '',
        grade: ''
      });
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage(error.response?.data?.message || 'An error occurred');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  return (
    <div className="change-grade-container">
      <div className="change-grade-card">
        <h2 className="change-grade-header">Grade Management</h2>

        <div className="operation-buttons">
          <button
            className={`operation-selector ${operation === 'add' ? 'active' : ''}`}
            onClick={() => setOperation('add')}
          >
            Add Grade
          </button>
          <button
            className={`operation-selector ${operation === 'delete' ? 'active' : ''}`}
            onClick={() => setOperation('delete')}
          >
            Delete Grade
          </button>
          <button
            className={`operation-selector ${operation === 'update' ? 'active' : ''}`}
            onClick={() => setOperation('update')}
          >
            Update Grade
          </button>
        </div>

        <form onSubmit={handleSubmit} className="grade-form">
          <div className="form-group">
            <label htmlFor="studentId">Student ID</label>
            <input
              type="text"
              id="studentId"
              name="studentId"
              value={formData.studentId}
              onChange={handleInputChange}
              className="form-input"
              placeholder="Enter student ID"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="courseId">Course ID</label>
            <input
              type="text"
              id="courseId"
              name="courseId"
              value={formData.courseId}
              onChange={handleInputChange}
              className="form-input"
              placeholder="Enter course ID"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="sectionId">Section ID</label>
            <input
              type="text"
              id="sectionId"
              name="sectionId"
              value={formData.sectionId}
              onChange={handleInputChange}
              className="form-input"
              placeholder="Enter section ID"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="semester">Semester</label>
            <input
              type="text"
              id="semester"
              name="semester"
              value={formData.semester}
              onChange={handleInputChange}
              className="form-input"
              placeholder="Enter semester"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="year">Year</label>
            <input
              type="text"
              id="year"
              name="year"
              value={formData.year}
              onChange={handleInputChange}
              className="form-input"
              placeholder="Enter year"
              required
            />
          </div>

          {(operation === 'add' || operation === 'update') && (
            <div className="form-group">
              <label htmlFor="grade">Grade</label>
              <input
                type="text"
                id="grade"
                name="grade"
                value={formData.grade}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Enter grade"
                required
              />
            </div>
          )}

          <button
            type="submit"
            className={`submit-button ${operation}`}
          >
            {operation.charAt(0).toUpperCase() + operation.slice(1)} Grade
          </button>
        </form>

        {message && (
          <div className={`message ${message.includes('successfully') ? 'success-message' : 'error-message'}`}>
            {message}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChangeGrade;
