import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function Dashboard() {
  const { state } = useLocation();
  const rollNo = state?.rollNo;
  const [student, setStudent] = useState(null);

  useEffect(() => {
    const fetchStudent = async () => {
      const response = await fetch(`http://localhost:5000/student/${rollNo}`);
      const data = await response.json();
      setStudent(data);
    };

    if (rollNo) fetchStudent();
  }, [rollNo]);

  if (!student) return <p>Loading...</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Welcome, {student.first_name} {student.last_name}</h2>
      <p><strong>Roll No:</strong> {student.roll_no}</p>
      <p><strong>Department ID:</strong> {student.department_id}</p>
      <p><strong>Batch:</strong> {student.batch}</p>
      <p><strong>Semester:</strong> {student.semester}</p>
      <p><strong>Email:</strong> {student.email}</p>
    </div>
  );
}

export default Dashboard;
