import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom'

const ChangeLogIn = () => {
  const [operation, setOperation] = useState(""); // "add", "delete", "update"
  const [formData, setFormData] = useState({
    rollNumber:"",
    newPassword:"",
    confirmPassword:""
  });
    const navigate = useNavigate();
      const [error, setError] = useState('');
      const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if(formData.newPassword!=formData.confirmPassword){
      setError('the given passwords dont match' );
      return
    }
      const response = await fetch('http://localhost:5000/changelogin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ rollNumber: formData.rollNumber,
            newPassword: formData.newPassword })
      });  
      const data = await response.json();

      if (response.ok) {
        setMessage('Password updated successfully!');
        setError('');
        setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state

      } else {
        setError(data.error || 'Failed to update password');
        setMessage('');
      }

  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Course Management</h2>
      {/* <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <button onClick={() => setOperation("add")}>Add Course</button>
        <button onClick={() => setOperation("delete")}>Delete Course</button>
        <button onClick={() => setOperation("update")}>Update Course</button> */}
      {/* </div> */}

      {(
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '400px' }}>
          <label>
            Roll Number:
            <input
              type="text"
              name="rollNumber"
              value={formData.rollNumber}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            New password:
            <input
              type="password"
              name="newPassword"
              value={formData.newPassword}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            confirm new password:
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
          </label>


          {/* <button type ="submit" onClick={() => navigate('/reset-password', { state: { rollNo:formData.rollNumber } })}>
            Reset Password
          </button> */}
          <button type="submit">
            {/* {operation === "add" && "Add Course"}
            {operation === "delete" && "Delete Course"}
            {operation === "update" && "Update Course"} */}
            {"reset password"}
          </button>
        </form>
      )}
       {message && <p>{message}</p>}
       {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );


}


export default ChangeLogIn;
