import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom'; // Use useNavigate instead of useHistory

function EnrolledCourses () {
  const { state } = useLocation();
  const rollNumber = state?.rollNo;

  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState([]);

  // Fetch enrolled courses on mount or when rollNumber changes
  useEffect(() => {
    const fetchEnrolledCourses = async () => {
      try {
        const response = await fetch(`http://localhost:5000/enrolled-courses/${rollNumber}`);
        const data = await response.json();

        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          console.error("Unexpected response format:", data);
          setCourses([]);
        }
      } catch (error) {
        console.error('Error fetching enrolled courses:', error);
      }
    };

    if (rollNumber) {
      fetchEnrolledCourses();
    }
  }, [rollNumber]);

  // Handle checkbox selection
  const handleCheckboxChange = (e, code) => {
    if (e.target.checked) {
      setSelectedCourses((prev) => [...prev, code]);
    } else {
      setSelectedCourses((prev) => prev.filter((c) => c !== code));
    }
  };

  // Handle course drop
  const handleDrop = async () => {
    try {
      const response = await fetch('http://localhost:5000/drop-courses', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          rollNumber,
          courses: selectedCourses, // should be teacher_course_codes
        }),
      });
  
      const data = await response.json();
  
      if (response.ok) {
        alert("Courses dropped successfully.");
        setSelectedCourses([]);
        setTimeout(() => window.location.reload(), 1500); // Optional: reload to sync state
      } else {
        alert(data.error || "Failed to drop courses.");
      }
    } catch (error) {
      console.error("Error dropping courses:", error);
      alert("Server error while dropping courses.");
    }
  };
  

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Your Enrolled Courses</h2>
      <table className="w-full border">
        <thead>
          <tr>
            <th>Select</th>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Elective</th>
          </tr>
        </thead>
        <tbody>
          {courses.length > 0 ? (
            courses.map((course) => (
              <tr key={course.teacher_course_code}>
                <td>
                <input
                  type="checkbox"
                  disabled={course.Type !== 'Elective'}
                  checked={selectedCourses.includes(course.teacher_course_code)}
                  onChange={(e) => handleCheckboxChange(e, course.teacher_course_code)}
                />
                </td>
                <td>{course.Course_code}</td>
                <td>{course.Course_name}</td>
                <td>{course.Type}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" className="text-center">No enrolled courses available.</td>
            </tr>
          )}
        </tbody>
      </table>
      <button
        onClick={handleDrop}
        className="mt-4 bg-red-500 text-white px-4 py-2 rounded"
        disabled={selectedCourses.length === 0}
      >
        Drop Selected
      </button>
    </div>
  );
};

export default EnrolledCourses;
