import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
const ChangeSection = () => {
   const [operation, setOperation] = useState(""); // "add", "delete", "update"
    const [formData, setFormData] = useState({
      teacherCourseCode: "",
      courseCode: "",
      teacherCode: "",
      availableSeats:""
    });

    const handleChange = (e) => {
      setFormData(prev => ({
        ...prev,
        [e.target.name]: e.target.value
      }));
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      if (operation === "add") {

        // call add API
        console.log("Add section:", formData);
      } else if (operation === "delete") {
        // call delete API
        console.log("Delete section:", formData.courseCode);
      } else if (operation === "update") {
        // call update API
        console.log("Update section:", formData);
      }
    };

    return (
      <div style={{ padding: '20px' }}>
        <h2>Course Management</h2>
        <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
          <button onClick={() => setOperation("add")}>Add Section</button>
          <button onClick={() => setOperation("delete")}>Delete Section</button>
          <button onClick={() => setOperation("update")}>Update Section</button>
        </div>

        {operation && (
          <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '400px' }}>
            <label>
              Teacher Course Code:
              <input
                type="text"
                name="teacherCourseCode"
                value={formData.teacherCourseCode}
                onChange={handleChange}
                required
              />
            </label>

            {(operation === "add" || operation === "update") && (
              <>
                <label>
                  Course Code:
                  <input
                    type="text"
                    name="courseCode"
                    value={formData.courseCode}
                    onChange={handleChange}
                    required={operation === "add"}
                  />
                </label>
                <label>
                  teacher code
                  <input
                    type="number"
                    name="teacherCode"
                    value={formData.teacherCode}
                    onChange={handleChange}
                    required={operation === "add"}
                  />
                </label>
                <label>
                  available seats
                  <input
                    type="number"
                    name="availableSeats"
                    value={formData.availableSeats}
                    onChange={handleChange}
                    required={operation === "add"}
                  />
                </label>
              </>
            )}

            <button type="submit">
              {operation === "add" && "Add Course"}
              {operation === "delete" && "Delete Course"}
              {operation === "update" && "Update Course"}
            </button>
          </form>
        )}
      </div>
    );
}

export default ChangeSection;
