import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EnrolledCourses = ({ rollNumber }) => {
  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState([]);

  useEffect(() => {
    axios.get(`/enrolled-courses/${rollNumber}`)
      .then(res => setCourses(res.data))
      .catch(err => console.error(err));
  }, [rollNumber]);

  const handleCheckboxChange = (courseCode) => {
    setSelectedCourses(prev =>
      prev.includes(courseCode)
        ? prev.filter(c => c !== courseCode)
        : [...prev, courseCode]
    );
  };

  const handleDrop = () => {
    axios.post('/drop-courses', {
      rollNumber,
      courses: selectedCourses
    }).then(() => {
      setCourses(prev => prev.filter(c => !selectedCourses.includes(c.Course_code)));
      setSelectedCourses([]);
    });
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Your Enrolled Courses</h2>
      <table className="w-full border">
        <thead>
          <tr>
            <th>Select</th>
            <th>Course Code</th>
            <th>Course Name</th>
            <th>Elective</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(course => (
            <tr key={course.Course_code}>
              <td>
                <input
                  type="checkbox"
                  disabled={course.Type !== 'Elective'}
                  checked={selectedCourses.includes(course.Course_code)}
                  onChange={() => handleCheckboxChange(course.Course_code)}
                />
              </td>
              <td>{course.Course_code}</td>
              <td>{course.Course_name}</td>
              <td>{course.Type}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleDrop} className="mt-4 bg-red-500 text-white px-4 py-2 rounded">
        Drop Selected
      </button>
    </div>
  );
};

export default EnrolledCourses;
